-- ----------------------------
-- Table structure for zh_admin
-- ----------------------------
DROP TABLE IF EXISTS `zh_admin`;
CREATE TABLE `zh_admin` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '管理员id',
  `username` varchar(50) NOT NULL COMMENT '管理员名称',
  `truename` varchar(50) NOT NULL COMMENT '管理员真实名称',
  `password` varchar(50) NOT NULL COMMENT '管理员密码',
  `gid` tinyint(3) NOT NULL COMMENT '管理员角色id',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '管理员状态：0为正常，1为禁用',
  `salt` char(20) NOT NULL COMMENT '加盐（生成随机数由于保护用户登录的安全性）',
  `login_ip` varchar(50) NOT NULL COMMENT '登录IP',
  `login_time` varchar(30) NOT NULL COMMENT '上次登录时间',
  `add_admin_id` tinyint(11) NOT NULL DEFAULT '0' COMMENT '执行添加管理员操作的管理员id',
  `add_time` varchar(30) NOT NULL COMMENT '注册时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='后台管理员表';

