DROP TABLE IF EXISTS `zh_category`;
CREATE TABLE `zh_category` (
  `id` int(8) NOT NULL AUTO_INCREMENT COMMENT '分类表（主要用于列表页文章分类）id',
  `pid` tinyint(4) NOT NULL COMMENT '上级菜单(父)id',
