DROP TABLE IF EXISTS `zh_community`;
CREATE TABLE `zh_community` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '留言表id',
  `name` char(50) NOT NULL COMMENT '用户名称',
  `email` char(30) NOT NULL COMMENT '邮箱',
  `phone` char(20) NOT NULL COMMENT '手机号码',
