-- ----------------------------
-- Table structure for zh_img
-- ----------------------------
DROP TABLE IF EXISTS `zh_img`;
CREATE TABLE `zh_img` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '图片管理表',
  `index_title` char(50) NOT NULL DEFAULT '' COMMENT '位置名称',
  `index_desc` char(30) NOT NULL DEFAULT '' COMMENT '位置说明',
  `index_main` char(50) NOT NULL DEFAULT '' COMMENT '展现形式',
  `index_num` tinyint(4) NOT NULL COMMENT '图片数量',
  `pid` tinyint(4) NOT NULL DEFAULT '0' COMMENT '上级菜单(父)id',
  `alt` char(50) NOT NULL DEFAULT '' COMMENT '当图片不存在或加载不成功要显示的图片信息',
  `url` varchar(200) NOT NULL DEFAULT '' COMMENT '图片链接地址',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态，是否显示禁用：0为显示，1为禁用',
  `sort` int(6) NOT NULL DEFAULT '0' COMMENT '排序',
  `add_time` char(30) NOT NULL COMMENT '添加时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=41 DEFAULT CHARSET=utf8;

