-- ----------------------------
-- Table structure for zh_list_page
-- ----------------------------
DROP TABLE IF EXISTS `zh_list_page`;
CREATE TABLE `zh_list_page` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级id',
  `nav_id` int(8) DEFAULT '0' COMMENT '导航id',
  `cid` tinyint(3) NOT NULL DEFAULT '0' COMMENT '分类表id,与分类表category关联',
  `title` varchar(255) DEFAULT NULL,
  `introduce` text COMMENT '介绍',
  `content` mediumtext COMMENT '内容',
  `pic_path` varchar(255) DEFAULT NULL,
  `pic_paths` varchar(255) DEFAULT NULL,
  `url` char(100) DEFAULT '',
  `page_title` varchar(200) DEFAULT NULL COMMENT '页面标题',
  `keywords` text,
  `description` text,
  `content_used` tinyint(1) unsigned NOT NULL DEFAULT '1',
  `pic_used` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `introduce_used` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `is_seo` int(1) NOT NULL DEFAULT '0',
  `language` varchar(255) DEFAULT NULL,
  `sort` int(6) NOT NULL DEFAULT '0' COMMENT '排序',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态：1为显示，0为禁用',
  `show_time1` char(30) NOT NULL DEFAULT '' COMMENT '活动开始时间',
  `show_time2` char(30) NOT NULL DEFAULT '' COMMENT '活动开始时间',
  `address` char(100) NOT NULL DEFAULT '' COMMENT '活动地点',
  `add_time` char(30) NOT NULL COMMENT '发布时间',
  `clicks` int(11) NOT NULL DEFAULT '0' COMMENT '阅读量',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐 0 否；1 是',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=34 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of zh_list_page
-- ----------------------------
INSERT INTO `zh_list_page` VALUES ('1', '0', '2', '0', '媒体发布', null, null, null, null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '1', '1', '1', null, '0', '1', '', '', '', '1569487442', '0', '0');
INSERT INTO `zh_list_page` VALUES ('2', '0', '3', '0', '视频播报', null, null, null, null, '', '视频播报—活动报道全媒体视频发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '1', '1', '1', null, '0', '1', '', '', '', '1569487465', '0', '0');
INSERT INTO `zh_list_page` VALUES ('3', '0', '4', '0', '活动预告', null, null, null, null, '', '活动预告—媒体管家全国重大公开活动预告发布！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '1', '0', '1', null, '0', '1', '', '', '', '1569487499', '0', '0');
INSERT INTO `zh_list_page` VALUES ('4', '0', '5', '0', '媒体资源', null, null, null, null, '', '媒体管家—全国媒体邀约、新闻发布、媒体公关服务商 - 媒体管家官网', '媒体记者邀约,媒体邀约,媒体邀请,记者邀约,媒体发布,网络公关,负面消除,负面压制,新闻记者邀约,媒体发布会记者邀约,媒体活动邀约', '媒体管家是是国内具有影响力的媒体传播机构，也是中国本土成立最早的公关传媒公司之一。自成立以来，媒体管家以其专业化标准化的服务体系帮助众多国内外知名企业和各类机构组织沟通品牌、推广产品、树立形象。', '0', '1', '1', '1', null, '0', '1', '', '', '', '1569487530', '0', '0');
INSERT INTO `zh_list_page` VALUES ('5', '1', '0', '2', '百鸟朝凤 凰者归来”丨八正8.18新品发布会现场报道', '将智慧融于创造，用心血浇筑产品！秉承坚定不移的信念，2019年8月18日，深圳八正品牌投资有限公司（以下简称八正）“百鸟朝凤，凰者归来”主题新品发布会在深圳宝安体育中心茶阅世界圆满落幕。', '&lt;p&gt;将智慧融于创造，用心血浇筑产品！秉承坚定不移的信念，&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;2019年8月18日，深圳八正品牌投资有限公司（以下简称八正）“百鸟朝凤，凰者归来”主题新品发布会在深圳宝安体育中心茶阅世界圆满落幕。&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ9195142918.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ9195142918.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 427px; width: 640px;&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ9195200162.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ9195200162.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 427px; width: 640px; display: inline;&quot;/&gt;&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;经过不断的探索与努力，&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;八正旗下湖村一号岽顶凤凰单丛茶系列、乐丛正宗凤凰单丛茶系列新品诞生！&lt;/strong&gt;而本次活动也得到了深圳潮青会相关领导、媒体、业内嘉宾及网友的高度认可。品牌的内涵诠释、产品的深度解析、温暖的故事分享让整个活动更加饱满。&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&amp;nbsp;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;01 一腔热血，弘扬传承&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;8月18日下午，八正执行董事王嘉霖先生在发布会上做分享，讲述了创立八正的初衷、品牌的核心优势以及他身为“继承人”在创业过程中的感悟和体会。&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ91952321N.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ91952321N.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 427px; width: 640px; display: inline;&quot;/&gt;&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;“我们的初衷很纯粹，我们想要让更多的寻常百姓都能喝上一杯正宗的凤凰单丛茶”，这是王嘉霖先生一直秉持的一句话。他相信在情怀和事业的结合下，更能在创新之中弘扬正宗的潮汕工夫茶文化！&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&amp;nbsp;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;02产品分享，亮点纷呈&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;新品发布会上，八正品牌运营官吴聪先生就产品力主题进行了分享。从洪荒蛮野再到文明初显，从文艺复兴发展史再到几何的玄妙，从天地人物法的实践再到天生天养之物的创造，从而延伸出“湖村一号“和“乐丛”新产品的产品力分析。&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ9195304260.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ9195304260.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 427px; width: 640px; display: inline;&quot;/&gt;&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;03新品首秀，震撼来袭&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;本次发布会在分享理论的同时，也为大家带来了两个品牌的新品，从包装再到细节之处都融汇了八正的心血：&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&amp;nbsp;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;“乐丛”享乐系列正宗凤凰单丛茶&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;①罐体形态的创新脱胎于达芬奇黄金分割理论；&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;②双开门礼盒的外包装印刻凤眼火瞳，好似月光倾洒湖面，波光粼粼，又如袖带轻飘的凤翎，瑞气呈祥；&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;③单罐茶礼可搭配便携式萃茶壶，贴心设计满足各种办公化场景，无时无刻，乐丛中来！&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ919534aE.jpg&quot; original=&quot;/uploads/allimg/190819/3-1ZQ919534aE.jpg&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 438px; width: 640px; display: inline;&quot;/&gt;&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;“湖村一号”凰者系列岽顶凤凰单丛茶&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;①湖村一号特供礼盒，运用掐丝珐琅工艺；&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;②外包装仿密陀彩绘漆箱，色彩典雅，更具收藏性；&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;③阴阳、凤凰、梧桐树、八卦等设计元素，寓意吉祥安宁。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ9195423958.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ9195423958.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 427px; width: 640px; display: inline;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;04茶道教学，茗香四溢&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;本次现场，八正特别邀请了潮汕工夫茶研究院院长林宇南先生做工夫茶文化的教学及分享，旨在让现场嘉宾及更多网友系统性了解凤凰单丛茶的知识及冲泡的细节。林宇南先生的专业性及权威性，无疑是本次发布会的亮点之一！&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ9195454Z6.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ9195454Z6.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 427px; width: 640px; display: inline;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;05品牌加盟，火爆延续&lt;/strong&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;台上的分享颇具震撼力，台下的精彩瞬间自然也不能错过。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ9195529324.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ9195529324.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 427px; width: 640px; display: inline;&quot;/&gt;&lt;/p&gt;&lt;p&gt;本次发布会针对业界各项需求推出多种加盟模式，为客户提供茶艺、销售、客户管理等专业培训，现场团队的解说也让客户更好地了解八正的品牌理念及产品的亮点。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190819/3-1ZQ919555LJ.png&quot; original=&quot;/uploads/allimg/190819/3-1ZQ919555LJ.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; max-width: 680px; height: 190px; width: 640px; display: inline;&quot;/&gt;&lt;/p&gt;&lt;p&gt;至此，八正新品发布会完美收官。未来，八正将高唱“传承“与”创新“的主旋律；让我们共同期待，八正为客户带来的更大价值！&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '', '', '', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569756545', '0', '1');
INSERT INTO `zh_list_page` VALUES ('6', '1', '0', '2', '天猫2019年度家电趋势发布，黑科技解锁个性生活', '5月17日，天猫联合大家电品牌，在杭州嘉里中心、杭州亲橙里举行了天猫2019年度家电趋势发布仪式，吸引了不少热衷智能新兴家电科技、追求智能家居生活的消费者们慕名而来。区别于传统模式，天猫在此次发布会现场打造了独具视觉冲击力的快闪店，通过2天的活动，为现场消费者提供了一次“解锁家电新趋势”的零距离互动+沉浸式体验之旅。此外，天猫还与百大家电品牌线上线下双向联动，共同关注智能家电，引领家电行业2019新趋势！', '&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;5月17日，天猫联合大家电品牌，在杭州嘉里中心、杭州亲橙里举行了天猫2019年度家电趋势发布仪式，吸引了不少热衷智能新兴家电科技、追求智能家居生活的消费者们慕名而来。区别于传统模式，天猫在此次发布会现场打造了独具视觉冲击力的快闪店，通过2天的活动，为现场消费者提供了一次“解锁家电新趋势”的零距离互动+沉浸式体验之旅。此外，天猫还与百大家电品牌线上线下双向联动，共同关注智能家电，引领家电行业2019新趋势！&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;天猫2019年度家电趋势发布，哈士奇黑科技加持解锁个性生活&quot; src=&quot;/ueditor/php/upload/image/20190929/1569756795100952.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;消费升级时代，智能家电成现代家电热选&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;消费升级时代，现代人群关注如何为自己打造更高品质生活，而智能家电无疑是提升生活质量的最佳选择，且已经成为了现代新家庭、现代家电需求首推的热门选择，家电的智能化也已经成为了一个不可逆转的趋势。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;家电行业新品类计划启动，解锁年轻消费群体个性生活&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;在上周，天猫消费电子举办第四届TES（天猫消费电子生态峰会），会上，天猫消费电子事业部总经理杨光表示，2019年将以“增长”为核心目标，把天猫打造为全球消费电子品牌第一增长平台，具体来说，天猫将吸引更多新用户、打造更多新品，帮助消费电子品牌实现业绩的持续增长。数据显示，天猫平台超过7500万的用户为重度新品消费人群，其中，超过一半是85后和90后。新品拉动了年轻用户群体的新品消费，并驱动品牌持续增长。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;天猫2019年度家电趋势发布，哈士奇黑科技加持解锁个性生活&quot; src=&quot;/ueditor/php/upload/image/20190929/1569756797163348.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;在5月17日发布仪式现场，天猫携手大家电品牌共同发布了“天猫2019年度家电趋势”，并宣布了“家电行业新品类计划”的正式启动。本次发布会分为“新品类”、“新科技”两大板块，杭州嘉里中心则是“新品类”家电的主场，天猫携手德国美诺Miele、小吉、方太、老板、大宇、哈士奇六大品牌在此打造了极具个性化的快闪体验店。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;2018年，天猫孵化了3500多个新品牌，另有539个新兴品类保持高速增长；2019年，天猫将聚焦天猫新品计划，在平台首发百万新品；启动全球招商计划，与全球品牌和产业带合作，预计孵化1000个新品类。家电的智能化，是为了满足现代人解放双手的需求，那智能家电的个性化，则更满足了年轻消费群体的“猎奇”心理。快闪体验店中的哈士奇经典轻奢复古冰箱、老板嵌入式蒸烤一体机“黑精灵”、大宇壁挂式洗衣机、小吉壁挂式洗衣机和化妆品冰箱、美诺进口家用干衣机、方太水槽洗碗机等个性化智能家电，通过左手黑科技右手高颜值的双重加持，成功吸引了全场年轻消费者的目光。哈士奇作为中国首批使用R290冷媒和全部使用环保材料生产的冰箱厂家之一。公司与可口可乐、百威、红牛、依云、喜力等世界各大知名啤酒饮料公司均有合作，为其提供近200种不同类型的制冷产品，产品远销美国、德国、南非、澳大利亚、巴西、英国等国家。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;天猫2019年度家电趋势发布，哈士奇黑科技加持解锁个性生活&quot; src=&quot;/ueditor/php/upload/image/20190929/1569756798666175.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;鲜声夺人！天猫探鲜派探索生活新鲜力&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;随着物质、精神生活水平的不断提升，现代人更加重视养生等健康问题，对饮食中的食物新鲜与时令性的要求也在不断提高，除了食物本身的新鲜应季，人们也更注重味觉与心理上的新奇体验。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;而本次发布会的另一大主题——“新科技”，以杭州亲橙里为主场，天猫家电携手天猫探鲜派（天猫食物保鲜联盟）在此为消费者们提供了食物保鲜的“秘诀”！天猫探鲜派（天猫食物保鲜联盟），是天猫携手9大冰箱品牌共同搭建的天猫探鲜派食物保鲜行业公益平台，旨在以天猫之势，与行业品牌携手打造关于食物保鲜的爆款内容，向大众普及一种更加新鲜时尚的生活方式，带领用户一起探索美食世界的奥秘。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;在快闪店现场，消费者们近距离体验了天猫探鲜派（天猫食物保鲜联盟）带来的全新生活方式，例如海尔时尚水吧家用冰箱搭载的外取水设计等；美菱十字双开门家用电冰箱的食材云保湿设计理念；松下升风冷变频多开门电冰箱的一键快速自动制冰等功能，均为消费者提供全方位安全健康的保鲜新体验。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;天猫2019年度家电趋势发布，哈士奇黑科技加持解锁个性生活&quot; src=&quot;/ueditor/php/upload/image/20190929/1569756800570773.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;零距离互动+沉浸式体验，你想要的家电都在这里&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;智能家电给予了现代人居家生活上的方便升级，更给予了消费者个人生活品质的提高，让人们解放双手的同时爱上居家、爱上生活，因此正值“520网络表白日”之际，活动现场开启了“520与天猫一起解锁家电新趋势，用家电说我爱你”主题打卡活动，和现场的消费者们一起通过零距离互动+沉浸式体验，用智能家电为爱发声，表白生活、表白未来。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;天猫2019年度家电趋势发布，哈士奇黑科技加持解锁个性生活&quot; src=&quot;/ueditor/php/upload/image/20190929/1569756801255953.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;一千个人有一万种表达爱的方式——用家电说我爱你，则代表着智能家电为人类提供更好的家居环境和居住体验的美好趋势。智能家居的未来趋势，是所有带电设备的联网化和数据化，同时，个性化、多元化、智能化，是天猫消费电子产品消费者需求呈现出的鲜明趋势。伴随着智能家居的普及和升级迭代，也代表着新的个性审美正在崛起，智能家电会逐渐从功能时代走向颜值时代，新兴品类打造个性生活的需求也在不断的增加中。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;消费升级的本职是供给侧升级，关键是找到各个圈层的消费起点，给他们更好的产品，而当下消费电子行业的供给侧改革有两大机会：一是消费者需求变化催生新的产品品类；二是智能化为产品性能提升带来的想象力。此次，天猫携手大家电品牌发布2019年度家电趋势，线上线下百大家电品牌同步联动，通过“新科技”鲜声夺人、用“新品类”打造个性生活，让“智能”、“黑科技”、“个性化”成为现代家电行业的新热词。消费升级、万物互联的时代已经到来，2019年和天猫家电一起，解锁智能家电新趋势！&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569756939', '0', '0');
INSERT INTO `zh_list_page` VALUES ('7', '1', '0', '2', '宜兴太阳季度假乐园在江苏宜兴发布', '2019年5月11日，以清净绮丽的山水竹海为依，融入法式生活美学的综合性亲子文旅度假村——宜兴太阳季度假乐园在江苏宜兴发布。该项目是由欧洲旅游地产行业翘楚法国PVCP集团，携手太华天谷国际生态度假小镇共同打造。太华天谷项目总占地面积约185万平方米，分三期开发，总投资达约80亿元，一期为宜兴太阳季度假乐园?书院度假村，二期为宜兴太阳季度假乐园。发布会现场，宜兴市人民政府副市长（党组成员）储红飙，宜兴市太华镇党委书记江峰，中国法国工商会总经理Caroline Penard（韦嘉玲）等领导到场，表达了对此次中法合作共创国际文旅项目的支持，以及对PVCP集团首站落地宜兴的信心。', '&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;2019年5月11日，以清净绮丽的山水竹海为依，融入法式生活美学的综合性亲子文旅度假村——宜兴太阳季度假乐园在江苏宜兴发布。该项目是由欧洲旅游地产行业翘楚法国PVCP集团，携手太华天谷国际生态度假小镇共同打造。太华天谷项目总占地面积约185万平方米，分三期开发，总投资达约80亿元，一期为宜兴太阳季度假乐园?书院度假村，二期为宜兴太阳季度假乐园。发布会现场，宜兴市人民政府副市长（党组成员）储红飙，宜兴市太华镇党委书记江峰，中国法国工商会总经理Caroline Penard（韦嘉玲）等领导到场，表达了对此次中法合作共创国际文旅项目的支持，以及对PVCP集团首站落地宜兴的信心。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;宜兴太阳季度假乐园，PVCP集团打造亲子度假新选择&quot; src=&quot;http://p9.pccoo.cn/news/20190512/2019051214331439420228_1466_832.png&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;法国PVCP集团（Pierre &amp;amp; Vacances-Center Parcs）是欧洲旅游地产开发及管理领军集团，成立于1967年，从事度假村的开发、销售以及运营管理50多年，是巴黎泛欧证券交易所上市公司。PVCP集团中国全资房地产投资子公司--璞蔚房地产CEO威廉（Guillaume Hubert de Fraisse）表示： “从1967年的阿沃里亚兹第一座无车滑雪度假村，到与欧洲迪士尼联合打造的欧洲大型生态亲子度假村--巴黎自然村庄，PVCP集团一直坚持绿色开发理念，在全球打造了超过280座度假村。”&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;伴随着中国的经济发展及二胎政策的放开，越来越多中产家庭在崛起，对亲子旅游的需求也日趋旺盛。据《2018携程酒店亲子房白皮书》数据，2018年中国亲子文旅市场规模超1000亿元，每年增速达60%，预计到2020年市场规模将达5000亿元。然而，国内现有的亲子主题旅游项目还远无法适应市场的繁荣需求，其中长三角地区的缺口较大。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;宜兴太阳季度假乐园，PVCP集团打造亲子度假新选择&quot; src=&quot;http://p9.pccoo.cn/news/20190512/2019051214334566367490_1320_788.png&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;因此PVCP集团于2016年正式进驻中国并布局10城，首度将欧洲知名亲子度假村品牌Sunparks太阳季度假乐园引入国内，并选择首站落地居于长三角优越位置的江苏无锡宜兴。选址宜兴，其交通规划发展是吸引客源的重要优势之一，宜兴位于上海，南京，杭州中间的理想地理位置。规划在建的宜长高速计划在2021年通车，届时从宜兴至南京、杭州的车程将缩短至约30分钟，计划2022年开通的沪苏湖高铁，将宜兴至上海的高铁行程缩短至约50分钟，完整构筑环太湖一小时经济圈，辐射长三角区域。优美的生态环境是选址宜兴的另一重要原因。项目所在地宜兴市太华镇拥有约10万亩竹林，境内荒山绿化覆盖率高，是远离城市喧嚣的天然氧吧。与此同时，宜兴拥有“大旅游”产业的独特资源，陶与茶的文化底蕴深厚，是家庭亲子度假的优选目的地。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;宜兴太阳季度假乐园，PVCP集团打造亲子度假新选择&quot; src=&quot;http://p9.pccoo.cn/news/20190512/2019051214340914818863_1416_702.png&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;据悉，PVCP集团和太华天谷将宜兴太阳季度假乐园分为两个阶段进行开发。此次发布的一期宜兴太阳季度假乐园?书院度假村，共有144套度假住宅，5种度假公寓及别墅类型，一期项目规划了超过3000平方米的公共配套设施及亲子主题活动区域，预计在2019年底2020年初开园。二期宜兴太阳季度假乐园，计划于3年落成，共约1000套度假住宅，将建设规划面积超过9500平方米，集零售、餐饮、娱乐设施于一体的穹顶市集，以及规划面积约8500平方米的水上乐园等设施。水上乐园为热带雨林风格，设有加热设施，保持全年恒温30摄氏度。水上乐园内将设有众多娱乐设施：水上滑梯、游泳池、按摩泳池等，让大人和孩子都能在此放松身心。PVCP集团有丰富的水上乐园开发经验，成功打造了巴黎自然村庄地热礁湖水上乐园、三森度假乐园水世界等欧洲大型水上乐园，这些经验将为宜兴项目水上乐园的建设提供重要支持。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;宜兴太阳季度假乐园，PVCP集团打造亲子度假新选择&quot; src=&quot;http://p9.pccoo.cn/news/20190512/2019051214355979713960_830_557.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;本着PVCP集团“欧洲自然度假生活”的理念，宜兴太阳季度假乐园?书院度假村的设计建造融合了东方意境与法式生活美学。度假村以静美的园林景观为主，辅以舒适关怀的室内设计，引入欧洲服务理念，其容积率约0.5，为度假者打造了一处诗意盎然的自然栖居。除此之外， PVCP集团为度假村设计了40余种亲子主题活动以及包括儿童水世界在内的多种户外主题活动配套，在大自然中为全年龄段客户筑一个关于欢乐的梦想。&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img alt=&quot;宜兴太阳季度假乐园，PVCP集团打造亲子度假新选择&quot; src=&quot;http://p9.pccoo.cn/news/20190512/2019051214363907163850_828_553.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;据PVCP集团介绍，一期宜兴太阳季度假乐园?书院度假村内的度假住宅也面向个人投资者发售。成为PVCP集团宜兴项目的业主后，将获得PVCP集团旗下在中国其他项目的度假交换权益及其他国际度假交换可能，为家庭开启国际亲子度假新体验。截至目前，PVCP集团在21个国家和地区，有逾280座度假村和度假酒店，管理约45000套物业，服务约18000位私人业主。此次PVCP集团在宜兴发布其中国首作，让中国家庭不出国门，即可体验法式亲子度假生活方式，享国际旅居投资体验。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569757059', '0', '0');
INSERT INTO `zh_list_page` VALUES ('8', '1', '0', '2', '“我们的记忆·旧闻新读”发布仪式成功举办', '2019年是中华人民共和国成立70周年，也是渡江战役胜利暨南京解放70周年。南京市新闻工作者协会联合南京市新媒体人士联谊会，在市委宣传部的统领下共同策划了“70年，我们的记忆·旧闻新读”活动。南京媒体人们以切实的方式纪念祖国成立70周年与南京解放70周年，图片与文字交织追溯了70年间精彩的故事。', '&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;2019年是中华人民共和国成立70周年，&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;也&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;是渡江战役胜利暨南京解放&lt;/span&gt;70周年。南京市新闻工作者协会联合南京市新媒体人士联谊会，在市委宣传部的统领下共同策划了“70年，我们的记忆·旧闻新读”活动。&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;南京媒体人们以切实的方式纪念祖国成立&lt;/span&gt;70周年与南京解放70周年，图片与文字交织追溯了70年间精彩的故事。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757128151342.jpg&quot; alt=&quot;图片故事回溯七十年岁月——“我们的记忆·旧闻新读”发布仪式成功举办&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;视觉南京暨&lt;/span&gt;“&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;70年，我们的记忆·旧闻新读&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;”发布仪式于5月7日早上在南京市新闻发布厅举办，&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;南京市委常委、宣传部部长陈勇；市委宣传部副部长、市委网信办主任王德生；市政府新闻办公室专职副主任汪晓燕；市委宣传部副巡视员鲍云海；南京报业集团党委书记、市记协主&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;席王嵬；南京报业集团党委委员、南京日报副总编辑陈曦以及南京市记者协会、南京新媒体人士联谊会的有关领导和嘉宾出席了仪式&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; color: rgb(43, 43, 43); letter-spacing: 0px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; color: rgb(43, 43, 43); letter-spacing: 0px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757128642722.jpg&quot; alt=&quot;图片故事回溯七十年岁月——“我们的记忆·旧闻新读”发布仪式成功举办&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;现场领导市委宣传部副部长、市委网信办主任王德生和南京报业集团党委书记、市记协主席王嵬启动&lt;/span&gt;“视觉南京”上线仪式&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em; color: rgb(43, 43, 43); letter-spacing: 0px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757129767385.jpg&quot; alt=&quot;图片故事回溯七十年岁月——“我们的记忆·旧闻新读”发布仪式成功举办&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em; color: rgb(43, 43, 43); letter-spacing: 0px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;《江苏商报》总编辑姜雷、《东方卫报》副总编辑薛巍、《现代家庭报》总编辑左元、南京日报社社会新闻部主任梁圣嵩作为专题指导老师，现场接受了聘书&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em; color: rgb(43, 43, 43); letter-spacing: 0px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757129108650.jpg&quot; alt=&quot;图片故事回溯七十年岁月——“我们的记忆·旧闻新读”发布仪式成功举办&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;随后&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;5家南京新媒体红色文化传播联盟单位代表&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;：&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;关卫东、李中兵、郭晓松、章文雷、刘争生&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;上&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;台展示签&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;好&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;单位名称的联盟旗帜&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; text-indent: 2em;&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;“70年，我们的记忆·旧闻新读”活动，由南京报业传媒集团资深编辑记者与新媒体小编们跨平台、跨媒介、跨体制编组，对70年里海量的新闻进行扫描，从中筛选出7条有代表性典型性的旧闻再回顾，对当年的新闻报道进行深度解读，通过亲历者回访口述、新闻细节重现、背景分析等。新媒体人士联谊会组成专门工作小组，充分听取专家团队选题会意见后，负责每期内容的采集与制作。每条被重新解读的旧闻通过各具特色的新媒体平台再次加工和传播，回眸70年风雨，呼唤共同记忆、展现当代价值。以此反映新闻事业、新闻人70年的变迁与传承，体现&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;了&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;主流媒体和社会自媒体的大融合。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757130639495.jpg&quot; alt=&quot;图片故事回溯七十年岁月——“我们的记忆·旧闻新读”发布仪式成功举办&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; list-style: none; border: none; outline: none; max-width: 580px;&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; white-space: normal; text-indent: 2em; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;此次&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;活动，由南京市委宣传部、南京市新闻办、南京市新闻工作者协会和南京市新媒体人士联谊会共同主办&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;，&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;承办单位为凤凰网江苏&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;。&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;今年&lt;/span&gt;4月开始启动，10月收官，每月1期，共7期。4月23日已率先在凤凰网推出了第一期，第一期的内容、特点、反响等情况姜雷姜总&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;也在现场作出了详细、精彩的点评&lt;/span&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;，他认为首期的四篇稿件从小切口进入，通过渡江胜利纪念章、南京广播电台旧址、烈士母亲、摄影图片等，从不同角度讲述了&lt;/span&gt;1949年南京解放这一重大历史事件背后的故事，容易引发读者共鸣。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569757167', '0', '0');
INSERT INTO `zh_list_page` VALUES ('9', '1', '0', '2', ' “一鹿有AI —2019康佳电视品牌之夜”发布会盛大召开', ' 4月28日晚上，在中国互联网与时尚潮流的汇集地北京朝阳区751D.PARK 79罐，一场“一鹿有AI ——2019康佳电视品牌之夜”发布会盛大开启。当天，康佳彩电亮点频出：不仅鹿晗首次以康佳彩电代言人的形象闪亮登场，康佳引领行业新趋势的全景AI电视A3也首次一展芳容，凭借囊括AI全程语音、AI画质、AI音质、AI音箱、AI智能家居、AI情感交互六大技术震撼全场。', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;4月28日晚上，在中国互联网与时尚潮流的汇集地北京朝阳区751D.PARK 79罐，一场“一鹿有AI ——2019康佳电视品牌之夜”发布会盛大开启。当天，康佳彩电亮点频出：不仅鹿晗首次以康佳彩电代言人的形象闪亮登场，康佳引领行业新趋势的全景AI电视A3也首次一展芳容，凭借囊括AI全程语音、AI画质、AI音质、AI音箱、AI智能家居、AI情感交互六大技术震撼全场。京东方科技集团总裁刘晓东、咪咕公司CMO况铁梅、埃及HOHO ELECTRIC&amp;amp;FURNITURE CO 董事长梁诚谦、阿里集团投资总监邓裕强、武汉工贸董事长李丰、国美零售控股有限公司高级副总裁李俊涛、爱奇艺高级副总裁段有桥、京东黑电总经理杨子盛、五星电器副总裁陈武俊、孟加拉ROYAL INTERNATIONAL(ELECTRO MART) CEO MOHAMMED NURUSSAFA MAZUMDER、联发科技电视事业部总经理张增军、南昌四平总经理谢为民、苏宁总裁助理宋辉，以及东道主康佳集团总裁周彬，康佳集团联席副总裁、深圳康佳电子科技有限公司总裁常东出席了当天的康佳电视品牌之夜。&lt;/p&gt;&lt;center style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;img width=&quot;600&quot; height=&quot;400&quot; src=&quot;https://res.daheapp.com/dhimg/1904282215142487.png?x-oss-process=style/picture_thum_600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none currentcolor; outline: none;&quot;/&gt;&lt;/center&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;在刚刚过去的2018年，康佳更取得了历史上最好的经营业绩，全年实现收入461.27亿元，同比增长47.71%，未来三年内要成为年收入千亿级企业。康佳围绕“科技+产业+园区”发展方向和“硬件+软件、终端+用户、科技+投控”发展模式，在持续壮大原有家电业务的同时，全力向战略性新兴领域布局和突破，已经构建形成了产业产品业务群、科技园区业务群、平台服务业务群和投资金融业务群等四大业务群协同发展的产业新格局，并且实现了经营业绩的高速增长和持续跨越，可以说康佳正在打造一个全新帝国。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;发布会上康佳集团总裁周彬表示：“现在的康佳是个快速增长，高效运转，共享双赢，年轻化发展的企业。而作为康佳立身之本的电视业务，不仅承载着康佳39年不平凡的发展历程，承载着康佳不断增长的用户资源和品牌影响，同时更承载着康佳在更大范围、更多维度上的产业延伸与战略协同的重要使命，是布局未来产业、实现跨越腾飞的坚实基础。在未来的发展中，我们将以更大的投入力度推动电视业务的做大做强，从研发投入、机制体制、业务模式、产业边界等方面予以全面赋能。签约鹿晗作为康佳电视代言人，正是我们在品牌投入上给予电视业务大力支持的有力证明。”&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;center style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;img width=&quot;600&quot; height=&quot;400&quot; src=&quot;https://res.daheapp.com/dhimg/1904282216393219.png?x-oss-process=style/picture_thum_600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none currentcolor; outline: none;&quot;/&gt;&lt;/center&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;签约鹿晗，康佳彩电品牌焕新迎来大动作&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;对于此次签约鹿晗成为代言人，康佳相关人士表示，鹿晗本人的形象以及近年的转型，都与康佳电视自身品牌形象和转型发展历程相稳合。鹿晗亲切温和的正面形象与康佳的科技创新型企业的定位相契合，鹿晗拥有庞大的年轻粉丝群也与康佳康佳品牌年轻化转型需要相呼应。可以预见，鹿晗的加盟将让康佳电视的品牌变得更加时尚化、娱乐化、年轻化，在80、90后主力消费群体中更加具有影响力。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;众所周知，实现品牌年轻化、与年轻消费者之间建立起更有效的沟通方式，已经成为中国消费产业在经济转型升级大背景下所面临的共同课题。可以说，谁拥有年轻消费群体就拥有更广阔的未来。彩电乃至家电行业是中国制造业的重要支柱，但如何让这一支柱产业更加焕发出时代生机则更是对全行业的考验。从这个角度看，签约鹿晗不仅是康佳电视年轻化品牌形象的开端，也是康佳集团更大的投入力度推动电视主业做大做强的具体体现。康佳集团总裁周彬在当晚的致辞中表示：“大股东华侨城集团已经明确将举全集团之力推动康佳的全面振兴，以更大的力度支持康佳的再次腾飞。在不忘彩电主业初心的同时，康佳集团还在以彩电为基础在更大范围、更多维度上的产业延伸与战略协同，围绕‘科技+产业+园区’发展方向和‘硬件+软件、终端+用户、科技+投控’发展模式快速迈进。”&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;center style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;img width=&quot;600&quot; height=&quot;401&quot; src=&quot;https://res.daheapp.com/dhimg/1904282217145948.png?x-oss-process=style/picture_thum_600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none currentcolor; outline: none;&quot;/&gt;&lt;/center&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;“实力派”康佳借力全景AI实现弯道提速&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;虽然凭借鹿晗的偶像魅力圈粉无数，但事实上，康佳在当晚推出的具备全景AI技术的A3彩电新品却是实实在在的“实力派”。A3集8K、全时AI(全时响应、全程控制)、IoT(场景化智能语音交互)等功能于一身，改变了彩电产品固有形态，奠定了互联生态技术基础，可以说是康佳让电视成为家庭AIoT中心、成为家庭万物互联入口的标志性产品。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;“全景AI，让家有AI”，是康佳在上个月的AWE大展上首次对外发布的彩电新战略。在常东看来，在当前人工智能和5G时代的风口下，“全景AI”将成为康佳弯道超车、重回彩电第一阵营的重要契机，同时也将率先引领整个彩电行业从“卖电视”到“卖场景”、面向AIoT实现转型。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;A3基于生活的时间轴，通过AI技术融入家庭生活场景，从时间、场景、操控、体验等多维度配合，共同构成的全景AI产品矩阵,囊括了当今最热门的AI全程语音、AI画质、AI音质、AI音箱、AI智能家居、AI情感交互六大技术，为用户带来无缝衔接。比如当晚康佳集团总裁周彬与康佳代言人鹿晗就对A3进行了现场“考试”，周彬只说了一句“想听鹿晗的歌”，A3就顺畅的播放出了鹿晗的《体会》;而身为2017年曼联大使的鹿晗也通过一句“我想看曼联的球赛”，就如愿欣赏到了A3为他选播的[英超]曼联VX曼城。如此“所说及所得”“所想及实现”的全景智能体验不仅让两位体验者颇为满意，也让在场观众惊叹不已。最终，这台A3也成为了鹿晗一份迟到了8天的生日礼物，虽然礼物晚了些，但仍让鹿晗激动不已。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;值得一提的是，这次康佳电视还提出正在打造全新“Aphaea”高端产品，致敬人类智慧文明史上伟大的创造，进一步探寻科技、艺术与人三者之间的关系，让“Aphaea”高端产品赋予全景AI技术、哲学、家庭、艺术、情感和美学，引领智慧生活，让家充满爱!A3就是基于“Aphaea”高端产品系列的代表之一。依托康佳30多年专注彩电的技术积淀，由康佳博士为首的专家团队耗资数百万元研发而成。这款集众优势于一身的芯片可广泛用于超高清行业中终端设备的8K信号处理，对行业具有重要意义。这也是从去年开始，康佳强化向彩电上游核心技术进发的重要成果。据悉，康佳集团去年的研发和技术创新总投入高达4.82亿元，掌握了近百项核心关键技术，其研发的彩电技术项目今年初还获得了国家科技最高奖项——国家科学技术进步奖，这也是康佳集团历史上获得的国家科技最高奖项，成为了中国彩电行业内赫赫有名的“实力派”。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;center style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;img width=&quot;600&quot; height=&quot;401&quot; src=&quot;https://res.daheapp.com/dhimg/1904282217553573.png?x-oss-process=style/picture_thum_600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none currentcolor; outline: none;&quot;/&gt;&lt;/center&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&lt;br style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;两名“年轻人”的互动，折射康佳创新变革新篇章&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;值得一提的是，在当晚的康佳彩电品牌之夜上，两大主角——康佳集团总裁周彬与康佳电视代言人鹿晗还面对产品主角康佳A3上演了一段精彩的“点球互动”。不知是不是巧合，周彬是康佳集团历任总裁中最年轻的一位，鹿晗也是康佳历任代言人中最年轻的一位，两个“年轻人”各自的临门一脚不仅为现场观众带来了惊喜的大奖，同时也用足球这种年轻人钟爱的方式，开启了康佳创新变革的新篇章。正如周彬所言，“年轻就有希望，创新就有可能。”&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 20px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(57, 57, 57);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;今年是实现“振兴康佳”目标的关键一年，在康佳企业内部和外部都激起了强大动能。人们有理由相信，39年前从各地聚集到中国改革开放最前沿深圳、创立起中国第一家合资电子企业康佳的那群年轻人，当时就已经把“创新变革”融入康佳的企业基因当中。如今，在创新变革理念的驱动下，以彩电为主体的康佳产业产品业务正协同科技园区业务、平台服务业务和投资金融业务四大业务群以“真的不一样”的全新面貌向着“千亿级康佳”全力冲刺。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体管家—全国媒体邀约、新闻发布、媒体公关服务商 - 媒体管家官网', '媒体记者邀约,媒体邀约,媒体邀请,记者邀约,媒体发布,网络公关,负面消除,负面压制,新闻记者邀约,媒体发布会记者邀约,媒体活动邀约', '体管家是是国内具有影响力的媒体传播机构，也是中国本土成立最早的公关传媒公司之一。自成立以来，媒体管家以其专业化标准化的服务体系帮助众多国内外知名企业和各类机构组织沟通品牌、推广产品、树立形象。', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569757296', '0', '1');
INSERT INTO `zh_list_page` VALUES ('10', '1', '0', '1', '2019张家港文化旅游推介会在常州、南京两地圆满举办', '张家港，一座鲜活的城市。这里的秋天是五彩斑斓的，在这里赏江南风光之美，品灿烂的文化历史，享人文风物之华。 9月9日刚结束的“鲜活张家港 人文满芳华”2019张家港文化旅游（常州）推介会的热度还未散去，10日下午，由张家港市文体广电和旅游局主办，同程旅游承办的张家港文化旅游推介会又在南京拉开序幕，将诗一样的五彩沙洲带给南京游客。', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px;&quot;&gt;张家港，一座鲜活的城市。这里的秋天是五彩斑斓的，在这里赏江南风光之美，品灿烂的文化历史，享人文风物之华。&lt;/p&gt;&lt;p sizcache0015406604579950134=&quot;24 62 264&quot; sizset=&quot;false&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img title=&quot;QQ截图20190911095113.png&quot; src=&quot;/ueditor/php/upload/image/20190929/1569757413580670.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;9月9日刚结束的“鲜活张家港 人文满芳华”2019张家港文化旅游（常州）推介会的热度还未散去，10日下午，由张家港市文体广电和旅游局主办，同程旅游承办的张家港文化旅游推介会又在南京拉开序幕，将诗一样的五彩沙洲带给南京游客。&lt;/p&gt;&lt;p sizcache0015406604579950134=&quot;24 62 267&quot; sizset=&quot;false&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;5.jpg&quot; src=&quot;/ueditor/php/upload/image/20190929/1569757413139853.jpg&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p sizcache0015406604579950134=&quot;24 62 269&quot; sizset=&quot;false&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;微信图片_20190911095241.jpg&quot; src=&quot;/ueditor/php/upload/image/20190929/1569757413109713.jpg&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;江苏省旅游协会副秘书长许家闻先生、张家港市文体广电和旅游局副局长杜文献先生、张家港各景区、酒店负责人，南京新闻媒体以及旅游业界的同行等参加了推介会。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;活动现场展播了永联小镇、凤凰山景区、双山香山旅游度假区、暨阳湖生态旅游区等文化旅游宣传视频，张家港的小镇风情、湖光山色犹如画卷徐徐展开。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;现场观众仿佛来到了如诗如画的秋之江南，听到了传承千年的河阳山歌，尝到了鲜美无比的江鲜美食，住进了惬意舒适的度假酒店……&lt;/p&gt;&lt;p sizcache0015406604579950134=&quot;24 62 275&quot; sizset=&quot;false&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;4.jpg&quot; src=&quot;/ueditor/php/upload/image/20190929/1569757414114696.jpg&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;“采菊东篱下，悠然见香山”。音画交织、情景交融的推介方式，将张家港旅游食、住、行、购、游、娱的各个方面展现得淋漓尽致，深入诠释了“鲜活张家港 人文满芳华”文旅品牌形象。一场视听盛宴，激起了大家对张家港文化旅游和休闲生活的向往之情。&lt;/p&gt;&lt;p sizcache0015406604579950134=&quot;24 62 278&quot; sizset=&quot;false&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;2.png&quot; src=&quot;/ueditor/php/upload/image/20190929/1569757414750560.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;推介会发布了张家港秋冬季主题游线路，诚挚邀请南京游客走进五彩沙洲，感受张家港文旅的独特魅力。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;此次在常州和南京举办的推介活动，不仅让当地市民直观的体验到了张家港文化旅游的独特魅力，也直接推动了两地游客来张家港旅游的热潮，同时此次活动也是“2019张家港秋游季”系列活动的重要组成部分。&lt;/p&gt;&lt;p sizcache0015406604579950134=&quot;24 62 284&quot; sizset=&quot;false&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); text-align: center;&quot;&gt;&lt;img title=&quot;3.jpg&quot; src=&quot;/ueditor/php/upload/image/20190929/1569757415754691.jpg&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;据悉，为打造文体旅融合新典范、献礼新中国成立七十周年的“2019张家港秋游季”系列活动将于本月下旬启动，涵盖“秋色满江红”“人文满芳华”“最美在金秋”三大篇章，举办旅游推介会、旅游直通车、双山香山红叶节、达人体验、梁丰生态园菊花展、各级各类重点体育赛事、凤凰镇美食节、常阴沙亲子体验等一系列丰富多彩的文体旅活动，深入推动文体旅融合，充分展现鲜活张家港的文化魅力、体育活力和旅游吸引力，让更多的游客走进张家港、爱上张家港。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); text-indent: 36px;&quot;&gt;为了扩大此次活动的影响力，张家港市文体广电和旅游局与同程旅游进行深度合作，通过线上、线下联动推广的新型营销模式，对“鲜活张家港”旅游品牌进行深层次、全方位的产品宣传，扩大了此次活动的影响范围。双方将继续深化合作，探索全域旅游时代的目的地营销的新模式、新思路，共同提升“鲜活张家港”的知名度和品牌影响力。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569757693', '0', '0');
INSERT INTO `zh_list_page` VALUES ('11', '1', '0', '1', '东方时尚季·第19届中国（青岛）国际时装周开幕 聚焦新锐力量·设计赋能商品力', '9月7日晚，东方时尚季?第19届中国（青岛）国际时装周在青岛西海岸红树林度假世界隆重开幕。中国纺织工业联合会会长孙瑞哲，青岛市委常委、常务副市长薛庆国，中国商业联合会副会长王耀，海南省文联副主席李福顺，中国服装设计师协会主席张庆辉，中国服装协会副会长周一奇，山东省工业和信息化厅二级巡视员张忠军，青岛市政协副主席、市工商联主席姜巧珍，青岛市工业和信息化局党组书记、局长姜波，', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;9月7日晚，东方时尚季?第19届中国（青岛）国际时装周在青岛西海岸红树林度假世界隆重开幕。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;中国纺织工业联合会会长孙瑞哲，青岛市委常委、常务副市长薛庆国，中国商业联合会副会长王耀，海南省文联副主席李福顺，中国服装设计师协会主席张庆辉，中国服装协会副会长周一奇，山东省工业和信息化厅二级巡视员张忠军，青岛市政协副主席、市工商联主席姜巧珍，青岛市工业和信息化局党组书记、局长姜波，中国国际贸易促进委员会山东省委员会副会长林源，山东工艺美术学校服装学院、山东省纺织服装行业协会有关领导，青岛市文化和旅游局和西海岸新区有关区市和部门领导，联合国教科文组织原助理总干事汉斯-道维勒，联合国高级经济学家梅里?玛达沙希，原国家外交部驻奥地利共和国大使卢永华，中国服装设计最高奖“金顶奖”获得者赵卉洲、李小燕、陈闻、刘洋、王玉涛，国内和青岛市有关高校、科研机构、行业协会、重点企业及中央、省驻青和本市媒体单位，共1000余人出席了开幕式。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 258&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757846872383.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;启动仪式&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;时尚能量球，点亮光明未来&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;本届时装周时间为9月6日至9月11日，以青岛西海岸红树林度假世界为主会场、青岛东方时尚中心（西海岸）为主秀场。由中国纺织工业联合会、青岛市人民政府主办，青岛市工业和信息化局、青岛西海岸新区管委、青岛市即墨区人民政府承办，中国服装协会、中国纺织工业联合会流通分会、中国服装设计师协会、山东省纺织服装行业协会支持，青岛中纺亿联时尚产业投资集团执行。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在现场，中国纺织工业联合会会长孙瑞哲，青岛市委常委、常务副市长薛庆国，青岛市政协副主席、市工商联主席姜巧珍，青岛市工业和信息化局党组书记、局长姜波，青岛西海岸新区副区长隋斌，青岛亿联控股集团有限公司董事长刘强共同为盛典启动仪式开幕。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;青岛市委常委、常务副市长薛庆国在致辞中提到，本届时装周聚焦“新锐力量·设计赋能商品力”主题，汇聚海内外新锐设计师和行业优势资源，努力在品牌培育、人才培养和产业转型等关键领域取得积极成效，推动一批重大时尚产业项目落地，加速形成人才培养新高地、产业转型新引擎、开放合作新平台。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 267&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757846131070.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;本届时装周共进行包括3位国际知名设计师、2位“金顶奖”设计师、4位中国十佳设计师和多位新锐设计师在内的40多场设计师及品牌发布秀，50多位设计师和50多个品牌静态展示，200多件古今中外艺术作品集中展出，累计2000多种各类展品商品参与展示和营销活动，60位超模全程助阵发布。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;组织了200余家全球知名采购商及300多位专业买手进行对接，助力品牌与消费者的互动与传播，加快拓展市场实现商业价值；线上整合了国内近百家网红直播平台，集聚国内外知名企业、设计师和品牌等优势资源，创新秀+展+直播售卖模式，构建起集展示、消费和交易为一体的商业展售平台，全方位打造新零售模式；同期举行的第四届GET WOW互联网时尚设计大赛，将进一步增强青岛对于新锐设计师的凝聚力和影响力。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;3场时装大秀&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;精妙绝伦营造流光之夜&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在希尼亚、韩后、FORDOO、FASHION SHOW、华韩整形、茶语·私享、中国移动、超级演说家（排名不分先后）的大力支持下，经过数月精心筹备，刘思聪、Eric Tibusch、赵卉洲等全球顶级设计师分别呈现了精彩绝伦的时装大秀，酷炫震撼的舞美、先锋前卫的设计、创意无限的舞蹈，营造出青岛的流光之夜，让人难以忘却。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;开幕秀设计师刘思聪为致敬这座滨海之城，带来主题为《自由》的系列秀。本系列通过印花的形式，把油画《海的女儿》贯穿在整个系列中，并提取油画中的颜色来作为主色调，给人一种画中人破纸而出的震撼美感。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 277&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757847346793.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在整体造型上，刘思聪的灵感源自自由女神，配合众多镜面材料，随着灯光的变幻营造出一种超现实的感官视觉。大量的金属和未来元素设计，彰显出穿衣者的鲜明个性和强烈的自我保护意识。设计者的奇思妙想和非凡品味，让全场赞叹，精彩的开端也让观众们更加期待后续节目。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 280&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757847551796.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;时尚产业需要对市场风向有敏锐的嗅觉，也需要来自国际潮流的交流碰撞，才能迸发出更多创意的火花。来自法国著名Haute Couture设计大咖Eric Tibusch，以精心准备的高品质国际大秀，为我们揭示法国现代女性对生活的态度和在社会中的地位。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 283&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757847543921.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Eric Tibusch深谙法国时尚精髓，将其织化在衣物上，通过截然不同的反差混搭方式生动演绎。他像是魔术师，以模特为载体，施展优雅、性感、活泼、开放、时尚和前沿相互交融一体的的时尚魔力，重新定义现代年轻女性。让观众身在青岛，心却已经漂洋过海，邂逅法国巴黎上流社会阶层女性的高贵、优雅和自信。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 286&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757847120544.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;2014APEC服装核心主创设计师之一、荣获2018金顶奖的顶尖设计师、EACHWAY艺之卉时尚集团创始人——赵卉洲，是今晚压轴秀《中国蓝》的设计师。通过传统中国蓝与新式色彩的碰撞，专属传统非遗图腾的新再现，艺术感找寻实用性的极简廓形，演绎过去与现代、历史与未来合二为一的时装境界。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 289&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757848836839.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;赵卉洲以中国蓝的力量诠释当代独立女性新姿态，不止步于外表，沉淀于衣褶间的精神与文化更是女性心灵深处的具象表达。EACHWAY带着最纯粹的初心，以中国蓝的概念展现非遗文化与当代时装精髓的相互融合。极具张力的原创力量以温婉的方式呈现给全场，不愧是现代女性的气质解读大师。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;“政府+园区+企业”&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;3到5年建设国际一流时装周&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;当前，青岛市委市政府正在大力推进建设国际时尚城的战略部署，按照王清宪书记树立平台思维，生态思维，创新市场化运作机制的指示要求，本届时装周应时举办，再次谋划创新转型。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;从今年起，时装周重点加强与青岛中纺亿联时尚产业投资集团投资建设的智慧型?全体验时尚创意产业生态园区“东方时尚中心”的深入合作，探索“政府+园区+企业”的运营模式。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 298&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757848302513.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;值得一提的是，OMC东方时尚中心是由青岛中纺亿联时尚产业投资集团投资建设的智慧型·全体验时尚创意产业生态园区。总投资人民币100亿，规划用地约1000亩，建筑面积约90万平方米，致力于打造“流行趋势的发布地、创意灵感的探寻地、时尚生活的体验地”，成为时尚、艺术、科技的国家级时尚创意产业标杆园区。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;通过龙头引领带动，统筹协调发挥区市和园区等各方资源力量，使时装周的时尚化、专业化、国际化、市场化在内容和形式上实现新的突破和提升。力争用3到5年时间，将青岛国际时装周打造为具有国际影响力、国内一流水平的时尚活动。&lt;/p&gt;&lt;p class=&quot;ql-align-center&quot; sizset=&quot;false&quot; sizcache003210401149519271=&quot;24 62 302&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190929/1569757848258965.jpeg&quot; max-width=&quot;600&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;全新运营模式下，本届活动将带来多轮惊喜。比如9月8日，在主会场青岛西海岸红树林度假世界召开“青岛国际时尚城建设与发展论坛”，9月8日至10日在主秀场青岛东方时尚中心（西海岸）举办国际时尚前沿日、新锐+时尚发布日、时尚青岛日和致敬盛典等多场次发布秀活动。此外，9月7日至9月11日，在主秀场青岛东方时尚中心（西海岸），新零售体验模式+直播的新型展销形式带领大家玩转时装周。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569757862', '0', '0');
INSERT INTO `zh_list_page` VALUES ('12', '1', '0', '1', '2019智慧文旅产业发展论坛暨第五期智慧旅游建设培训班在上海隆重召开', '9月4日，2019智慧文旅产业发展论坛暨第五期智慧旅游建设培训班在上海开班。中国旅游报社党委书记、社长徐行，上海狂龙数字科技股份有限公司董事长杨雅姝，张江集团副总经济师、上海张江文化控股有限公司董事长韩露出席并致辞。文化和旅游部信息中心副主任信宏业，北京大学教授、北京大学旅研与规划中心主任、国际旅游学会创会主席吴必虎，北京第二外国语学院教授、文化和旅游部“十三五”规划专家委员会委员张凌云等专家学者出席并进行主题演讲。', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 25px; font-size: 14px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;9月4日，2019智慧文旅产业发展论坛暨第五期智慧旅游建设培训班在上海开班。中国旅游报社党委书记、社长徐行，上海狂龙数字科技股份有限公司董事长杨雅姝，张江集团副总经济师、上海张江文化控股有限公司董事长韩露出席并致辞。文化和旅游部信息中心副主任信宏业，北京大学教授、北京大学旅研与规划中心主任、国际旅游学会创会主席吴必虎，北京第二外国语学院教授、文化和旅游部“十三五”规划专家委员会委员张凌云等专家学者出席并进行主题演讲。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://qiye.lnd.com.cn/uploads/allimg/190906/4-1ZZ6094J9612.png&quot; data-bd-imgshare-binded=&quot;1&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;本期培训班由中国旅游报社主办，上海狂龙数字科技股份有限公司承办，大地风景文旅集团、中国智慧旅游产业联盟、上海迪望旅游文化股份有限公司、上海长沪文化旅游发展有限公司协办。来自全国各地文化旅游部门、旅游景区、旅游企业集团、院校、专业机构有关负责人等300余位代表参加了本次培训。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://qiye.lnd.com.cn/uploads/allimg/190906/4-1ZZ6094K3560.png&quot; data-bd-imgshare-binded=&quot;1&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;中国旅游报社党委书记、社长徐行在致辞中表示，党的十九大报告提出要推动互联网大数据人工智能和实体经济的深度融合，加快建设网络强国、数字中国、智慧社会，互联网时代技术互补渗透，资本汇聚集中，发展智慧旅游关乎文化和旅游产业的现代化、智能化，既是提升文旅产业作为现代服务业素质的必然要求，也是文旅产业所处时代发展进步的客观要求。互联网时代，文旅产业延伸融合发展是大势所趋，依托大数据、人工智能、5G等先进技术的应用，为游客提供更加便利便捷、智慧、智能的旅行服务，是旅游产业发展和提升的重要路径。&lt;/span&gt;&lt;br data-filtered=&quot;filtered&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; color: rgb(82, 82, 82); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;/&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;徐行表示，进行智慧文旅产业培训，学习智慧文旅建设思路方法，探索智慧文旅建设应用模式，应符合四大需要。第一，这是顺应信息科技发展，促进文旅产业进步的需要。第二，这是深化文旅交流合作，推动产业跨界融合的需要。第三，这是适应新时代文旅消费场景需求，满足人民群众对美好生活向往的需要。第四，这是理论与实战结合，行业自我提升的需要。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://qiye.lnd.com.cn/uploads/allimg/190906/4-1ZZ6094KY35.png&quot; data-bd-imgshare-binded=&quot;1&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;上海狂龙数字科技股份有限公司董事长杨雅姝在致辞中表示，随着国家大力推行文化和旅游产业的发展，文旅融合将影响未来十年中国旅游产业发展的重大变革。作为智慧文旅服务运营商，上海狂龙充分重视并积极把握这一时代性和历史性的转折和机遇，提出“文化为心、旅游为体、智慧为用”，以满足消费者需求为出发点，用科技赋能文旅，实现文化资源与旅游资源的深度融合，在促进经济增长的同时，推动旅游产业与文化产业进一步的发展，与各位同行携手共创文旅产业新业态。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://qiye.lnd.com.cn/uploads/allimg/190906/4-1ZZ6094P2B2.png&quot; data-bd-imgshare-binded=&quot;1&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;张江集团副总经济师、上海张江文化控股有限公司董事长韩露在致辞中介绍，张江通过27年的发展，已成为国家产业创新、科技研究的重要载体，张江除了重点发展软件和集成电路产业、生命科学产业以外，于2004年着手发展文化创意和数字信息产业。如今，张江有2000多家文化创意企业。大家耳熟能详的盛大游戏、喜马拉雅、哔哩哔哩等企业，已构成了张江文化创业的主力军。近日，国办出台了关于进一步促进文化和旅游消费的意见，文化和旅游二者的融合，如今既有组织上的保障，又有制度上的机遇。通过今天的交流会，聆听在场各位专家的的真知灼见，相信我们一定能把这块文化旅游的蛋糕越做越大。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://qiye.lnd.com.cn/uploads/allimg/190906/4-1ZZ6094PC63.png&quot; data-bd-imgshare-binded=&quot;1&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;上海狂龙数字科技股份有限公司CTO吴志刚分享了有关于狂龙数字智慧文旅建设的思考，并进行了中国智慧旅游产业联盟长三角中心成立的授牌仪式。“中国智慧旅游产业联盟长三角中心”是中国智慧旅游产业联盟在长三角地区成立的集技术创新、产业服务与资源交流为目的的平台；该中心依托“狂龙数字、迪望文旅、杭州酥游”等长三角核心文旅创新产业成员单位，实现开放性运作。&lt;/span&gt;&lt;br data-filtered=&quot;filtered&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; color: rgb(82, 82, 82); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;/&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;会议现场，专家学者和业界代表围绕“人工智能时代下的智慧文旅融合发展探讨”“科技如何赋能文旅融合发展”等话题，展开了圆桌对话，现场干货满满。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://qiye.lnd.com.cn/uploads/allimg/190906/4-1ZZ6094Q0423.png&quot; data-bd-imgshare-binded=&quot;1&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;本期培训班将持续至9月5日。其间，文化和旅游部信息中心副主任信宏业，北京大学教授、国际旅游学会创会主席吴必虎，世界旅游城市联合会专家委员会副主任、北京交通大学旅游系主任张辉，北京第二外国语学院教授张凌云，中国社会科学院旅游研究中心特约研究员蔡家成，北京第二外国语学院教授厉新建，中国智慧旅游产业联盟秘书长蒋骏，复旦大学旅游学系副教授胡安安等业界资深专家将依次为学员授课。授课专家将分别围绕“智慧推进智慧文旅建设”“5G时代的产品创新和政策响应”“ 大数据时代与大众化旅游”“智慧旅游发展趋势与展望”“全域旅游与智慧旅游”“ 文旅融合与科技赋能”“智慧旅游建设方法与实战案例”“浅谈大数据思维与创新”等主题向学员们授业解惑。&lt;/span&gt;&lt;br data-filtered=&quot;filtered&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; color: rgb(82, 82, 82); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;/&gt;&lt;span style=&quot;color: rgb(82, 82, 82); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;智慧文旅产业发展论坛暨智慧旅游建设培训班是由中国旅游报社策划组织，面向全国各级文化和旅游行政部门、智慧旅游试点城市、智慧旅游企业、各地 A 级旅游景区及文化旅游企事业单位的全国性培训项目。自2014年以来，大会已在北京、郑州、杭州、龙岩成功举办了四期，邀请了产学研等资深人士参会演讲、专业授课、交流分享，获得各地文旅部门和专业机构的鼎力支持和积极参与，受到业界广泛欢迎和好评，累计培训 2000 余人。培训班旨在为全国各地文化和旅游信息化建设提供理论经验和实践指导，提升产业价值，提高服务水平，推动文化和旅游行业信息化发展，为各文化和旅游部门与智慧旅游企业搭建一个合作、学习和交流的平台。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://qiye.lnd.com.cn/uploads/allimg/190906/4-1ZZ6094Q55I.png&quot; data-bd-imgshare-binded=&quot;1&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569757927', '0', '0');
INSERT INTO `zh_list_page` VALUES ('13', '1', '0', '1', '“悦享好年华，益智常相伴”阿尔茨海默症系列活动圆满落幕', '为提高阿尔茨海默症的国民认知度，做到“早预防、早发现、早治疗”，由华润置地悦年华·颐养中心（广州越秀）携手广州医科大学附属第五医院康复医学科举办的“悦享好年华，益智常相伴”阿尔茨海默症科普系列活动，于2019年9月21日圆满落幕。', '&lt;p&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; color: rgb(82, 82, 82); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none; color: rgb(51, 51, 51); font-size: small;&quot;&gt;为&lt;/span&gt;&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;提高阿尔茨海默症的国民&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;认知度&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;，做到“&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;早&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;预防、&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;早发现&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;、早治疗”&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;，由&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;华润置地悦年华&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;·颐养&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;中心（&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;广州&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;越秀）&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;携手&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;广州医科大学附属第五医院&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;康复医学科举办&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;的“&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;悦享好年华，&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;益智常相伴”&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;阿尔茨海默症科普&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;系列活动，于&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;2019年9月21日&lt;/em&gt;&lt;em style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;圆满落幕。&lt;/em&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;9月21日是“世界阿尔茨海默症日”（也称“世界老年痴呆日”），目前中国有老年痴呆患者1000万人之多，占世界总病例数的四分之一，平均每年有30万新发病例，且中国老年痴呆的患病率已随着年龄的升高呈显著增长趋势。然而，很多人甚至是部分医师到目前对于该疾病的认识仍存在大量误区，导致我国阿尔茨海默症就诊率和治疗率非常低，为社会和家庭造成沉重的负担。&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;/p&gt;&lt;table cellspacing=&quot;0&quot; cellpadding=&quot;0&quot;&gt;&lt;tbody style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;tr style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot; class=&quot;firstRow&quot;&gt;&lt;td height=&quot;0&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border-width: initial; border-style: none; border-color: initial; outline: none;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;td height=&quot;0&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border-width: initial; border-style: none; border-color: initial; outline: none;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;td style=&quot;padding: 0px; margin: 0px; list-style: none; border-width: initial; border-style: none; border-color: initial; outline: none;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px; margin: 0px; list-style: none; border-width: initial; border-style: none; border-color: initial; outline: none;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190923/5-1Z923113123M6.png&quot; original=&quot;/uploads/allimg/190923/5-1Z923113123M6.png&quot; style=&quot;padding: 0px; margin: 0px; list-style: none; border: currentcolor; outline: none; width: 1269px; height: 952px; display: inline; max-width: 680px;&quot;/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;为加强广大社会群众对阿尔茨海默症的认识，华润置地悦年华·颐养中心（广州越秀）携手广州医科大学附属第五医院康复医学科资深医师团队开展“悦享好年华，益智常相伴”科普系列活动。广州医科大学附属第五医院康复医学科科主任欧海宁、广州医科大学附属第五医院康复医学科主治医师梁俊杰、华润置地悦年华·颐养中心（广州越秀）项目营销负责人武丽梅等出席本次活动，就阿尔茨海默症的早预防、早发现、早治疗，以及老年人品质生活的打造进行深入交流。&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190923/5-1Z923113139209.png&quot; original=&quot;/uploads/allimg/190923/5-1Z923113139209.png&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); width: 1269px; height: 1692px; color: rgb(51, 51, 51); display: inline; max-width: 680px;&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;在科普讲座中，欧海宁主任以《留住记忆，遇见美好》为题做分享，从电影《依然爱丽丝》的情节切入，跟随患阿尔茨海默症女主角的症状与遭遇深入浅出的进行讲解。&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;欧海宁主任表示，阿尔茨海默症的症状大致分为三种:&amp;nbsp;进行性记忆减退、认知障碍、精神行为改变。进行性记忆衰退主要表现为记忆力的迅速衰退，出现长期记忆强，但短期记忆弱的情况。例如患者也许能够回忆十几年甚至几十年前的事件，但数小时前的记忆却模糊不清；认知障碍则意味着患者的记忆力、注意力、推理能力、语言能力等多种认知能力出现问题；精神行为异常包含情感的异常症状、精神病症状等，患者会出现妄想、幻觉、情感性格突变，从开朗突然变得安静沉默，还可能出现异常行为。当这三类症状在老年人身上显现的时候，一定要给予足够的重视，尽早到医院进行检测与治疗。&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;梁俊杰医生以《认知功能常用评估方法》为题，分享如何尽可能早的发现病情并开始治疗。诊断最重要的信息提供者应是老年人自身与照料者，信息包括性格变化、行为变化、“忘事”程度的逐渐加深等。诊断过程主要使用心理学评估量表，除此之外，长者步态分析也是阿尔茨海默症的诊断方法之一。&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190923/5-1Z923113149421.png&quot; original=&quot;/uploads/allimg/190923/5-1Z923113149421.png&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); width: 1269px; height: 952px; color: rgb(51, 51, 51); display: inline; max-width: 680px;&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;医者仁术更仁心，科普讲座结束后，为实现“理论结合实际”，切实解决与会长者的问题，广州医科大学附属第五医院康复医学科资深医师团队进行免费义诊咨询活动。现场以简单有趣的拼图游戏、连字游戏与读色游戏等作为阿尔茨海默症的“检测试卷”，由资深医师团队初步判断是否存在阿尔茨海默症的风险与征兆，并对后续跟踪检测提供指导与支持。&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190923/5-1Z92311315UV.png&quot; original=&quot;/uploads/allimg/190923/5-1Z92311315UV.png&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); width: 1205px; height: 904px; color: rgb(51, 51, 51); display: inline; max-width: 680px;&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;img alt=&quot;&quot; src=&quot;http://biztoutiao.com/uploads/allimg/190923/5-1Z923113211338.png&quot; original=&quot;/uploads/allimg/190923/5-1Z923113211338.png&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); width: 1226px; height: 818px; color: rgb(51, 51, 51); display: inline; max-width: 680px;&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;作为活动主办方之一的华润置地悦年华·颐养中心（广州越秀）项目，是广州市越秀区民政局，以PPP模式授权华润置地投资、建设、运营的，以“养、食、住、娱”四大生活场景为核心的，以品质设计与景观疗愈为特色的全龄段专业照护型颐养中心。在活动现场，项目营销负责人为与会长者详细介绍华润置地康养业务发展、悦年华·颐养中心（广州越秀）项目计划为长者打造的优雅、自然、健康的品质生活场景。&lt;/span&gt;&lt;br style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);&quot;&gt;秉承央企社会责任感，华润置地悦年华·颐养中心（广州越秀）希望借助世界阿尔茨海默症日，加强广大群众对阿尔茨海默症的认知，做到“早预防、早发现、早治疗”，解放长者及其家庭，真正做到悦享美好年华！&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569758004', '0', '0');
INSERT INTO `zh_list_page` VALUES ('14', '1', '0', '1', '一分钟带你走进鸣诺医药智能仓产品发布会', '2019年9月21日，鸣诺科技&amp;雨柔科技 2019医药智能仓产品发布会在上海举行, 上海鸣诺网络科技有限公司与雨柔科技等众多领导出席了此次盛会，并发表了重要讲话，还邀请到广大媒体朋友参与此次盛会共同开启宏伟篇章。近些年来,随着线上流量红利消褪以及传统零售行业进入增长减速时期,无论是线上电商还是线下实体零售都需要新的增长点,而新零售的出现对二者而言无异于一剂良药。在这样的大背景下,新零售这股浪潮涌向各个行业的同时,也开始向医药行业渗透。', '&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;2019年9月21日，鸣诺科技&amp;amp;雨柔科技 2019医药智能仓产品发布会在上海举行, 上海鸣诺网络科技有限公司与雨柔科技等众多领导出席了此次盛会，并发表了重要讲话，还邀请到广大媒体朋友参与此次盛会共同开启宏伟篇章。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;国民所趋，智能再升级，一分钟带你走进鸣诺医药智能仓产品发布会&quot; src=&quot;http://p3.pstatp.com/large/pgc-image/e4479afe5d0d413da3a504c3742230e0&quot; original=&quot;http://p3.pstatp.com/large/pgc-image/e4479afe5d0d413da3a504c3742230e0&quot; img_width=&quot;6240&quot; img_height=&quot;4160&quot; inline=&quot;0&quot; style=&quot;padding: 0px; margin: 10px auto; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; display: block; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: transparent; height: auto !important;&quot;/&gt;&lt;/p&gt;&lt;p class=&quot;pgc-img-caption&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 16px; font-size: 12px; text-align: center; color: rgb(119, 119, 119); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;近些年来,随着线上流量红利消褪以及传统零售行业进入增长减速时期,无论是线上电商还是线下实体零售都需要新的增长点,而新零售的出现对二者而言无异于一剂良药。在这样的大背景下,新零售这股浪潮涌向各个行业的同时,也开始向医药行业渗透。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;在欧美医药流通模式中，药店是核心渠道，约占美国医药零售市场约60-70%的份额，而在中国零售端占比仅为20%。基本保持年均4%的增速，预计到2020年，美国零售药店市场规模将超过3,800亿美元。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;国民所趋，智能再升级，一分钟带你走进鸣诺医药智能仓产品发布会&quot; src=&quot;http://p3.pstatp.com/large/pgc-image/b681e62a263546cfb1789c91f955ee54&quot; original=&quot;http://p3.pstatp.com/large/pgc-image/b681e62a263546cfb1789c91f955ee54&quot; img_width=&quot;876&quot; img_height=&quot;582&quot; inline=&quot;0&quot; style=&quot;padding: 0px; margin: 10px auto; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; display: block; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: transparent; height: auto !important;&quot;/&gt;&lt;/p&gt;&lt;p class=&quot;pgc-img-caption&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 16px; font-size: 12px; text-align: center; color: rgb(119, 119, 119); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;虽然目前中美医药流通体制差异大，发展阶段、行业政策与竞争环境也不尽相同，但医药零售产业的基础运营逻辑、发展规律与核心本质是趋于一致的。尤其是从2018年开始，新零售持续引发行业变革，阿里、腾讯等巨头企业的布局，扰动着整体行业的竞争格局，医药零售作为零售行业重要的组成部分，变革已势在必行，上海鸣诺网络科技有限公司则给中国医药零售企业做出了很好的示范。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;国民所趋，智能再升级，一分钟带你走进鸣诺医药智能仓产品发布会&quot; src=&quot;http://p1.pstatp.com/large/pgc-image/fd5cdd97ce314a56b0262e9d00eaa359&quot; original=&quot;http://p1.pstatp.com/large/pgc-image/fd5cdd97ce314a56b0262e9d00eaa359&quot; img_width=&quot;821&quot; img_height=&quot;544&quot; inline=&quot;0&quot; style=&quot;padding: 0px; margin: 10px auto; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; display: block; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: transparent; height: auto !important;&quot;/&gt;&lt;/p&gt;&lt;p class=&quot;pgc-img-caption&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 16px; font-size: 12px; text-align: center; color: rgb(119, 119, 119); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;上海鸣诺网络科技有限公司经多年不断技术攻关，AI自助售药机设备已领先 行业同类型产品多个世代，成功迈入4.0范畴。独家研发 药房专用ERP系统，助力药房实现新时代；新零售产业升级。鸣诺药房专用ERP系统功能强大，单一系统覆盖药房原有物流系统，进销系统，药品分类系统，医保系统等9大系统。方便操作，真正实现药房数据全打通。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;此外,医药行业和新零售又是近年来资金偏好的投资领域,在强大的市场需求和资本力量的推动下, 上海鸣诺网络科技有限公司走到了行业变革的前沿。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;国民所趋，智能再升级，一分钟带你走进鸣诺医药智能仓产品发布会&quot; src=&quot;http://p3.pstatp.com/large/pgc-image/0dd029ae1e354674aecc8b7ae23677a4&quot; original=&quot;http://p3.pstatp.com/large/pgc-image/0dd029ae1e354674aecc8b7ae23677a4&quot; img_width=&quot;501&quot; img_height=&quot;292&quot; inline=&quot;0&quot; style=&quot;padding: 0px; margin: 10px auto; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; display: block; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: transparent; height: auto !important;&quot;/&gt;&lt;/p&gt;&lt;p class=&quot;pgc-img-caption&quot; style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none; line-height: 16px; font-size: 12px; text-align: center; color: rgb(119, 119, 119); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;上海鸣诺网络科技有限公司是国内第一家专注于帮助基层医疗单位实现信息化，数字化，智能化的综合服务商。自建互联网医院，助力基层医疗单位实现云升级，为普通患者提供云问诊，云开方。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 16px; margin-bottom: 16px; font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; list-style: none; border: none; outline: none; line-height: 25px; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: transparent;&quot;&gt;随着鸣诺科技&amp;amp;雨柔科技 2019医药智能仓产品发布会在上海举行，该公司也将持续为国家健康事业做出贡献。&lt;/p&gt;&lt;p&gt;&lt;img alt=&quot;国民所趋，智能再升级，一分钟带你走进鸣诺医药智能仓产品发布会&quot; src=&quot;http://p3.pstatp.com/large/pgc-image/9305754d73a9453686e1e47a6895a988&quot; original=&quot;http://p3.pstatp.com/large/pgc-image/9305754d73a9453686e1e47a6895a988&quot; img_width=&quot;824&quot; img_height=&quot;544&quot; inline=&quot;0&quot; style=&quot;padding: 0px; margin: 10px auto; font-family: 微软雅黑; list-style: none; border: currentcolor; outline: none; display: block; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: transparent; height: auto !important;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '媒体首发—全行业活动媒体报道全媒体发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569758192', '0', '0');
INSERT INTO `zh_list_page` VALUES ('15', '2', '0', '0', '【优酷视频】非物质文化遗产成果展示暨永乐古窖新品鉴赏会在京举行', '', '&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;iframe src=&quot;http://player.youku.com/embed/XNDE0OTExOTk0OA==&quot; width=&quot;760&quot; height=&quot;360&quot; scrolling=&quot;no&quot; frameborder=&quot;0&quot; align=&quot;&quot;&gt;&lt;/iframe&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;2019年4月21日“非物质文化遗产成果展示暨永乐古窖新品鉴赏会”在千年帝都北京隆重举行。第十二届全国政协副主席齐续春，中央编办原副主任、第十二届全国人大内务司法委员会委员、现中国轻工业联合会会长张崇和，中国文学艺术界联合会原党组书记胡振民，中国商业联合会原会长何济海，原国家文化部副部长、国家博物馆首任馆长潘震宙，全国妇联原副主席、中国关心下一代工作委员会常务副主任刘晓莲将军，军事科学院原副部长刘金胜将军，原第二炮兵副司令员李龙虎将军及其他军界领导。还有轻工业及酒类协会于学军副秘书长、王延才理事长、刘员副会长等酒行业领导。及四川航空董事长蓝新国、原宜宾市市长吴光镭、宜宾市工商联主席王力平、宜宾市副秘书长蒋明、及四川宜宾市叙州区党委书记丁应虎、叙州区副区长郭孝华、屏山县王坚副书记、酒促局兰国宾副局长等其他多位宜宾政府领导和广东政府各级领导出席。更有考古界多位知名专家及各行业领导出席本次盛会。这座明代的古窖池向世人揭示了作为中国世界文化遗产的价值所在，也向来自各界的菁英大咖展示了永乐古窖酒酿酒技艺作为非物质文化遗产的宝贵之处，同时这场汇聚众多关注的鉴赏会也重新定义了”非遗白酒“的价值高度。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758335236674.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;一、高规格高层次交流盛会，传递非遗民族文化价值新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;本次发布会的举办地在千年帝都北京，历经辽、金、元、明、清的都城，其设计规划体现了中国古代城市规划的最高成就。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;如今永乐古窖在这里举行新品鉴赏会，把民族非物质文化遗产成果展现在各界菁英面前，使白酒类非物质文化遗产的品质传递又上升到了新的高度。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;文化部副部长、中共文化部党组成员潘震宙在品鉴会上发言，表示永乐古窖充分发挥生产优势，实现非遗技术的“活态传承”，即有利于增强非遗的活力，又有利于提高传承人的传承积极性，还有利于弘扬传统优秀文化，更有利于促进文化消费、扩大就业。他表示“要把非物质文化遗产和人民生活紧密融合起来，让非物质文化遗产和非物质文化遗产都‘活起来’，这是最好的保护，最好的传承，最好的发展。”&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;在潘震宙先生看来，我们的祖辈用勤劳和智慧创造了辉煌灿烂的文化遗产，永乐古窖是其中的一部分，对待这些穿越600年岁月的老窖池，让它们与产品、品牌相融合，是一个 “好归宿”。他为永乐古窖题字“永存真善美，乐道古今情”，希望永乐古窖能够一直保持匠心，传递匠艺。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758336598141.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758336127417.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;二、&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span class=&quot;Apple-converted-space&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;高速度入选中国世界文化遗产预备名录，代表历史传承价值新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;永乐古窖的发现及发掘震惊了中国考古界，其所在糟坊头是“中国八大白酒老作坊之一“，它的出现把四川这一地区的酿酒有实物可证的历史向前推进了170年。2012年11月国家文物局公布永乐古窖所在糟坊头老作坊进入中国世界遗产预备名单，据考古专家高大伦表示这是迄今为止最早最快进入名单的窖池遗址。如今永乐古窖进入世界文化遗产名录的申报工作正在进行当中，永乐古窖有望成为最早进入“世界文化遗产名录”的酿酒窖池，进一步提升中国在世界酿酒史上的地位。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;宜宾市副市长、市工商联主席、市总工商会长王力平表示“宜宾的古老窖池未曾间断过生产，佐证着宜宾灿烂悠久的历史文化，对于更好地传播宜宾酒文化，本次永乐古窖在北京举办这次发布会，非常有意义。”&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758337406736.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;三、高稀缺性的&lt;/span&gt;“双遗“资源，收获业界肯定新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;在酒类行业市场竞争日趋激烈的环境下，白酒“双遗”的市场价值不言而喻，以“双遗”为载体，白酒品牌对于文化核心价值的挖掘将水到渠成。而“双遗”身份之于中国白酒的市场价值，又不仅仅局限于历史和酿造工艺层面，更可以延伸至文化品位的精神享受，由对历史的尊敬、对文化的传承，表现为中国白酒对品质的坚持，对完美的追求，并将之转化为品牌个性与行业核心竞争力。在会上，轻工业联合会会长张崇和发表演讲，对永乐古窖的“双遗产“价值给予了高度肯定，并表示”永乐古窖历史悠久，文化底蕴丰厚，又是拥有双遗产的珍稀老酒企，希望永乐古窖能够持续稳定发展。同时对永乐古窖未来的发展提出了三点期待“一要传承经典、创新发展，继承古法技艺，创新改良方法，不断壮大；二要精益求精，酿造竞品，发扬工匠精神，追求卓越技艺，不断提升白酒品质；三要弘扬文化，守护精华，丰富白酒内涵，增加白酒底蕴，不断丰富和繁荣中国白酒文化”。希望永乐古窖为发展壮大酒产业，发扬光大酒文化，为满足人民美好生活向往，不断作出新的更大的贡献。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758337832234.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;四、高真实度年份酒，充实非遗白酒品质支撑新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;永乐古窖老酒系列新品创造了行业中高端白酒基酒年份最长、占比最高的纪录，刷新了行业年份白酒的品质价值，也使“非遗白酒“不再是徒有文化内涵，而是拥有真正的硬核实力。永乐古窖老酒系列100%使用5年以上地藏原酒作为基酒，并加入10年以上地藏老酒精华，使每一滴永乐古窖酒均带有舒适陈香，这在行业当中是十分罕见的，在行业中能够拥有这样规模老酒储备的企业屈指可数，而愿意把这些老酒作为基酒的企业也只有永乐古窖一家，永乐古窖希望能够在拥有历史文化内涵的同时用真正的高品质留住消费者，为消费者提供真实的年份白酒。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758337118255.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758338276436.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;五、高价值五粮浓香型白酒，收获各界盛赞新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;作为一款高价值的五粮浓香白酒，永乐古窖收获了业界大咖的高度盛赞，著名白酒专家、四川省酿酒协会专家组组长曾祖训，与全国白酒专业委员会专家、国家级评酒委员赖登燡评价永乐古窖酒“窖香优雅，陈香舒适“，并表示这是在浓香白酒中不常见的优质香气，同时也体现了永乐古窖酒的高品质价值。永乐古窖的高价值与高品质不仅收获了业内人士的高端赞扬，同时也收获了诸多书画界、政治界、文化界名人的高度赞扬，他们对永乐古窖酒的品质大加赞扬，区广安甚至亲自赋诗作画对永乐古窖酒表示赞赏，称赞其“莫羡邻居开窖早，但求陈厚不嫌迟”。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190929/1569758338172299.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;永乐古窖以强大的资源优势和硬核实力赢得了社会各界的高度赞扬，同时向行业内外证明了永乐古窖品牌资产价值巨大，发展潜力无限。未来永乐古窖也将持续关注非物质文化遗产的相关话题，积极投身到非物质文化遗产的保护和传承工作中去，与各界携手共同弘扬中华民族优秀文化传统，发扬中华民族工匠精神，持续为消费者提供更高品质的产品，酿造“老百姓喝得起的好酒”！&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '', '', '', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569758754', '0', '0');
INSERT INTO `zh_list_page` VALUES ('16', '2', '0', '0', '【腾讯视频】永乐古窖在千年帝都北京定义“非遗白酒”价值新高度', '2019年4月21日“非物质文化遗产成果展示暨永乐古窖新品鉴赏会”在千年帝都北京隆重举行。第十二届全国政协副主席齐续春，中央编办原副主任、第十二届全国人大内务司法委员会委员、现中国轻工业联合会会长张崇和，中国文学艺术界联合会原党组书记胡振民，中国商业联合会原会长何济海，原国家文化部副部长、国家博物馆首任馆长潘震宙，全国妇联原副主席、中国关心下一代工作委员会常务副主任刘晓莲将军，', '&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;iframe src=&quot;https://v.qq.com/txp/iframe/player.html?vid=y0863cg856l&quot; width=&quot;760&quot; height=&quot;360&quot; scrolling=&quot;no&quot; frameborder=&quot;0&quot; align=&quot;&quot;&gt;&lt;/iframe&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;2019年4月21日“非物质文化遗产成果展示暨永乐古窖新品鉴赏会”在千年帝都北京隆重举行。第十二届全国政协副主席齐续春，中央编办原副主任、第十二届全国人大内务司法委员会委员、现中国轻工业联合会会长张崇和，中国文学艺术界联合会原党组书记胡振民，中国商业联合会原会长何济海，原国家文化部副部长、国家博物馆首任馆长潘震宙，全国妇联原副主席、中国关心下一代工作委员会常务副主任刘晓莲将军，军事科学院原副部长刘金胜将军，原第二炮兵副司令员李龙虎将军及其他军界领导。还有轻工业及酒类协会于学军副秘书长、王延才理事长、刘员副会长等酒行业领导。及四川航空董事长蓝新国、原宜宾市市长吴光镭、宜宾市工商联主席王力平、宜宾市副秘书长蒋明、及四川宜宾市叙州区党委书记丁应虎、叙州区副区长郭孝华、屏山县王坚副书记、酒促局兰国宾副局长等其他多位宜宾政府领导和广东政府各级领导出席。更有考古界多位知名专家及各行业领导出席本次盛会。这座明代的古窖池向世人揭示了作为中国世界文化遗产的价值所在，也向来自各界的菁英大咖展示了永乐古窖酒酿酒技艺作为非物质文化遗产的宝贵之处，同时这场汇聚众多关注的鉴赏会也重新定义了”非遗白酒“的价值高度。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819861292271.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;一、高规格高层次交流盛会，传递非遗民族文化价值新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;本次发布会的举办地在千年帝都北京，历经辽、金、元、明、清的都城，其设计规划体现了中国古代城市规划的最高成就。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;如今永乐古窖在这里举行新品鉴赏会，把民族非物质文化遗产成果展现在各界菁英面前，使白酒类非物质文化遗产的品质传递又上升到了新的高度。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;文化部副部长、中共文化部党组成员潘震宙在品鉴会上发言，表示永乐古窖充分发挥生产优势，实现非遗技术的“活态传承”，即有利于增强非遗的活力，又有利于提高传承人的传承积极性，还有利于弘扬传统优秀文化，更有利于促进文化消费、扩大就业。他表示“要把非物质文化遗产和人民生活紧密融合起来，让非物质文化遗产和非物质文化遗产都‘活起来’，这是最好的保护，最好的传承，最好的发展。”&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;在潘震宙先生看来，我们的祖辈用勤劳和智慧创造了辉煌灿烂的文化遗产，永乐古窖是其中的一部分，对待这些穿越600年岁月的老窖池，让它们与产品、品牌相融合，是一个 “好归宿”。他为永乐古窖题字“永存真善美，乐道古今情”，希望永乐古窖能够一直保持匠心，传递匠艺。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819861253328.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819862845574.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;二、&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span class=&quot;Apple-converted-space&quot; style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;高速度入选中国世界文化遗产预备名录，代表历史传承价值新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;永乐古窖的发现及发掘震惊了中国考古界，其所在糟坊头是“中国八大白酒老作坊之一“，它的出现把四川这一地区的酿酒有实物可证的历史向前推进了170年。2012年11月国家文物局公布永乐古窖所在糟坊头老作坊进入中国世界遗产预备名单，据考古专家高大伦表示这是迄今为止最早最快进入名单的窖池遗址。如今永乐古窖进入世界文化遗产名录的申报工作正在进行当中，永乐古窖有望成为最早进入“世界文化遗产名录”的酿酒窖池，进一步提升中国在世界酿酒史上的地位。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;宜宾市副市长、市工商联主席、市总工商会长王力平表示“宜宾的古老窖池未曾间断过生产，佐证着宜宾灿烂悠久的历史文化，对于更好地传播宜宾酒文化，本次永乐古窖在北京举办这次发布会，非常有意义。”&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819862346577.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;三、高稀缺性的&lt;/span&gt;“双遗“资源，收获业界肯定新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;在酒类行业市场竞争日趋激烈的环境下，白酒“双遗”的市场价值不言而喻，以“双遗”为载体，白酒品牌对于文化核心价值的挖掘将水到渠成。而“双遗”身份之于中国白酒的市场价值，又不仅仅局限于历史和酿造工艺层面，更可以延伸至文化品位的精神享受，由对历史的尊敬、对文化的传承，表现为中国白酒对品质的坚持，对完美的追求，并将之转化为品牌个性与行业核心竞争力。在会上，轻工业联合会会长张崇和发表演讲，对永乐古窖的“双遗产“价值给予了高度肯定，并表示”永乐古窖历史悠久，文化底蕴丰厚，又是拥有双遗产的珍稀老酒企，希望永乐古窖能够持续稳定发展。同时对永乐古窖未来的发展提出了三点期待“一要传承经典、创新发展，继承古法技艺，创新改良方法，不断壮大；二要精益求精，酿造竞品，发扬工匠精神，追求卓越技艺，不断提升白酒品质；三要弘扬文化，守护精华，丰富白酒内涵，增加白酒底蕴，不断丰富和繁荣中国白酒文化”。希望永乐古窖为发展壮大酒产业，发扬光大酒文化，为满足人民美好生活向往，不断作出新的更大的贡献。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819863680934.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;四、高真实度年份酒，充实非遗白酒品质支撑新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;永乐古窖老酒系列新品创造了行业中高端白酒基酒年份最长、占比最高的纪录，刷新了行业年份白酒的品质价值，也使“非遗白酒“不再是徒有文化内涵，而是拥有真正的硬核实力。永乐古窖老酒系列100%使用5年以上地藏原酒作为基酒，并加入10年以上地藏老酒精华，使每一滴永乐古窖酒均带有舒适陈香，这在行业当中是十分罕见的，在行业中能够拥有这样规模老酒储备的企业屈指可数，而愿意把这些老酒作为基酒的企业也只有永乐古窖一家，永乐古窖希望能够在拥有历史文化内涵的同时用真正的高品质留住消费者，为消费者提供真实的年份白酒。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819863108181.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819863701331.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px; font-family: 微软雅黑; list-style: none; border: none; outline: none;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;五、高价值五粮浓香型白酒，收获各界盛赞新高度&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;作为一款高价值的五粮浓香白酒，永乐古窖收获了业界大咖的高度盛赞，著名白酒专家、四川省酿酒协会专家组组长曾祖训，与全国白酒专业委员会专家、国家级评酒委员赖登燡评价永乐古窖酒“窖香优雅，陈香舒适“，并表示这是在浓香白酒中不常见的优质香气，同时也体现了永乐古窖酒的高品质价值。永乐古窖的高价值与高品质不仅收获了业内人士的高端赞扬，同时也收获了诸多书画界、政治界、文化界名人的高度赞扬，他们对永乐古窖酒的品质大加赞扬，区广安甚至亲自赋诗作画对永乐古窖酒表示赞赏，称赞其“莫羡邻居开窖早，但求陈厚不嫌迟”。&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; text-align: center; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;img alt=&quot;比故宫还早3年的永乐古窖在千年帝都北京定义“非遗白酒”价值新高度&quot; src=&quot;/ueditor/php/upload/image/20190930/1569819864626155.jpg&quot; style=&quot;padding: 10px; margin: 0px 0px 0px -2em; font-family: 微软雅黑; list-style: none; border: 0px currentcolor; outline: none; max-width: 580px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px 0px 20px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体; list-style: none; border: none; outline: none; line-height: 28px; font-size: 14px; font-variant-numeric: normal; font-variant-east-asian: normal; white-space: normal; color: rgb(51, 51, 51); text-indent: 2em; widows: 1; font-stretch: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-family: 宋体; list-style: none; border: none; outline: none; font-size: 19px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; list-style: none; border: none; outline: none;&quot;&gt;&lt;/span&gt;&lt;/span&gt;永乐古窖以强大的资源优势和硬核实力赢得了社会各界的高度赞扬，同时向行业内外证明了永乐古窖品牌资产价值巨大，发展潜力无限。未来永乐古窖也将持续关注非物质文化遗产的相关话题，积极投身到非物质文化遗产的保护和传承工作中去，与各界携手共同弘扬中华民族优秀文化传统，发扬中华民族工匠精神，持续为消费者提供更高品质的产品，酿造“老百姓喝得起的好酒”！&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2019-09-26/5d8ccb3d863c0.png', null, '', '视频播报—活动报道全媒体视频发布平台！', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布', '媒体邀约、媒体发布、全媒体发布、新闻发布、新闻营销、新闻软文发布、软文发布，上海软闻网络，媒体管家', '1', '0', '0', '0', null, '0', '1', '', '', '', '1569820030', '0', '0');
