DROP TABLE IF EXISTS `zh_navigation`;
CREATE TABLE `zh_navigation` (
  `id` int(8) NOT NULL AUTO_INCREMENT COMMENT '首页导航表',
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '上级id',
  `title` varchar(255) NOT NULL COMMENT '导航名称',
  `url` char(50) NOT NULL DEFAULT '#' COMMENT '链接地址',
  `sort` int(6) NOT NULL DEFAULT '10' COMMENT '排序',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐 0 否；1 是',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

