-- ----------------------------
-- Table structure for zh_single_page
-- ----------------------------
DROP TABLE IF EXISTS `zh_single_page`;
CREATE TABLE `zh_single_page` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级id',
  `title` varchar(255) DEFAULT NULL,
  `nav_id` int(8) DEFAULT '0' COMMENT '导航id',
  `introduce` text COMMENT '介绍',
  `content` mediumtext COMMENT '内容',
  `pic_path` varchar(255) DEFAULT NULL,
  `pic_paths` varchar(255) DEFAULT NULL,
  `url` char(100) DEFAULT '',
  `page_title` varchar(200) DEFAULT NULL COMMENT '页面标题',
  `keywords` text,
  `description` text,
  `content_used` tinyint(1) unsigned NOT NULL DEFAULT '1',
  `pic_used` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `introduce_used` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `url_used` tinyint(1) NOT NULL DEFAULT '0',
  `is_seo` int(1) NOT NULL DEFAULT '0',
  `language` varchar(255) DEFAULT NULL,
  `sort` int(6) NOT NULL DEFAULT '0' COMMENT '排序',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态：0为显示，1为禁用',
  `add_time` char(30) NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=44 DEFAULT CHARSET=utf8;

