INSERT INTO `zh_single_page` VALUES ('11', '0', '关于我们', '8', null, null, null, null, '', '关于我们 - 媒体管家官网', '媒体记者邀约,媒体邀约,媒体邀请,记者邀约,媒体发布,网络公关,负面消除,负面压制,新闻记者邀约,媒体发布会记者邀约,媒体活动邀约,', '媒体管家是是国内具有影响力的媒体传播机构，也是中国本土成立最早的公关传媒公司之一。自成立以来，媒体管家以其专业化标准化的服务体系帮助众多国内外知名企业和各类机构组织沟通品牌、推广产品、树立形象。', '1', '0', '0', '0', '1', null, '0', '0', '');
INSERT INTO `zh_single_page` VALUES ('14', '0', '联系我们', '9', null, null, null, null, '', '媒体管家—专业的媒体邀约、新闻软文发布、媒体公关服务机构！', '媒体记者邀约,媒体邀约,媒体邀请,记者邀约,媒体发布,网络公关,负面消除,负面压制,新闻记者邀约', '媒体管家（zhaomedia.com）是上海软闻网络科技有限公司旗下品牌，是国内具有影响力的媒体传播机构，也是中国本土成立最早的公关传媒公司之一。自成立以来，媒体管家以其专业化标准化的服务体系帮助众多国内外知名企业和各类机构组织沟通品牌、推广产品、树立形象。', '0', '1', '1', '0', '1', null, '0', '0', '');
INSERT INTO `zh_single_page` VALUES ('9', '0', '服务项目', '6', null, null, null, null, '', '媒体管家—专业的媒体邀约、新闻软文发布、媒体公关服务机构！', '媒体记者邀约,媒体邀约,媒体邀请,记者邀约,媒体发布,网络公关,负面消除,负面压制,新闻记者邀约,', '媒体管家（zhaomedia.com）是上海软闻网络科技有限公司旗下品牌，是国内具有影响力的媒体传播机构，也是中国本土成立最早的公关传媒公司之一。自成立以来，媒体管家以其专业化标准化的服务体系帮助众多国内外知名企业和各类机构组织沟通品牌、推广产品、树立形象。', '0', '1', '1', '0', '1', null, '0', '0', '');
